


const a = require("lodash");


console.log("it's alive!!");

function getType(vrijednost){
console.log(`Ulazna vrijedost za provjeru je "${vrijednost}"`);
if (a.isNumber(vrijednost)){
    console.log(`>>Ulazna vrijedost je broj`);
}
if (a.isString(vrijednost)){
    console.log(`>>Ulazna vrijedost je string`);
}
if (a.isArray(vrijednost)){
    console.log(`>>Ulazna vrijedost je array`);
}
if (a.isObject(vrijednost)){
    console.log(`>>Ulazna vrijedost je Object`);
}
}

const myNumbers = [1, 9, 5, 2, 8];


const myObject = {ime:"Marko"}

getType(1);
getType("neka riječ");
getType(myNumbers);
getType(myObject);


const sortedNumbers=a.sortBy(myNumbers);
console.log(sortedNumbers);
console.log(sortedNumbers);